import React, { useState } from 'react'
import Editor from './Editor';


function App() {
  const [html, setHtml] = useState('')
  const [css, setCss] = useState('')
  const [js, setJs] = useState('')

  const srcDoc = 
  <html>
    <body>${html}</body>
    <style>${css}</style>
    <script>${js}</script>
  </html>

  return (
    <div>
      <div className= "pane top-pane">
        <Editor 
          language="xml" 
          displayTitle="HTML" 
          value={html} 
          onChange={setHtml} 
        />
        <Editor 
          language="css" 
          displayTitle="CSS" 
          value={css} 
          onChange={setCss}
        />
        <Editor 
          language="javascript" 
          displayTitle="JS" 
          value={js} 
          onChange={setJs} 
        />
      </div>
      <div className= "pane">
        <iframe
          title="result"
          sandbox="allow-scripts"
          frameBorder="0"
          width="100%"
          height="100%"
        />
      </div>
    </div>
  );
}

export default App;
